INSERT INTO `Post` (`idUser`, `idCategory`, `name`, `slug`, `content`, `creationDate`) VALUES ('1', '1', 'Zend Form', 'zend-form', 'fPHP Markdown to HTML converter\n------------------------------\n\n```php\n<?php\n\nnamespace Application\\Form;\n\n\nuse Zend\\Form\\Element\\Checkbox;\nuse Zend\\Form\\Element\\Email;\nuse Zend\\Form\\Element\\Password;\nuse Zend\\Form\\Form;\nuse Zend\\InputFilter\\Input;\n\nclass SignInForm extends Form\n{\n\n    /**\n     * {@inheritDoc}\n     */\n    public function __construct()\n    {\n        parent::__construct();\n\n        $this->add(new Email(\'email\'));\n        $this->add(new Password(\'password\'));\n        $this->add(new Checkbox(\'remember_me\'));\n\n        $inputFilter = $this->getInputFilter();\n\n        $emailInput = new Input(\'email\');\n        $emailInput->setRequired(true);\n        $inputFilter->add($emailInput);\n\n        $passwordInput = new Input(\'password\');\n        $passwordInput->setRequired(true);\n        $inputFilter->add($passwordInput);\n\n    }\n}\n```', '2017-08-22 15:18:25');
